import React, {Component} from 'react';

import { Card,
         CardImg,
         CardText,
         CardBody,
         CardTitle,
         CardSubtitle,
         Col,
         Button } from 'reactstrap';

import {Link} from 'react-router-dom';
import {ProductConsumer} from '../context';

import "../App.css";

class Product extends Component {
  render () {
    const {id, name, img, price, inCart} = this.props.product;
    return (
      <Col  key={id} xs={12} sm={12} md={12} lg={4}>
      <Card outline color="info" className="box">
        <CardImg top width="5%" src={img} alt="Card image cap" />
        <CardBody>
          <CardTitle>{name}</CardTitle>
          <CardSubtitle>Rp {price} ;-</CardSubtitle>
          <br/>
          <Button href="/productdetail"outline color="danger" block>Detail</Button>
        </CardBody>
      </Card>
     </Col>

   );
  };
}

export default Product;
