import React, {Component} from 'react';
import { Card,
         CardImg,
         CardText,
         CardBody,
         CardTitle,
         CardSubtitle,
         Col,
         Button } from 'reactstrap';

import {Link} from 'react-router-dom';
import {ProductConsumer} from '../context';

class Product extends Component {
  render () {
    const {id, name, img, price, inCart} = this.props.product;
    return (
      <Col  key={id} xs={12} sm={12} md={12} lg={4}>
      <Card>
        <CardImg top width="100%" src="https://placeholdit.imgix.net/~text?txtsize=33&txt=318%C3%97180&w=318&h=180" alt="Card image cap">{img}</CardImg>
        <CardBody>
          <CardTitle>Card title</CardTitle>
          <CardSubtitle>Card subtitle</CardSubtitle>
          <CardText>Some quick example text to build on the card title and make up the bulk of the card's content.</CardText>
          <Button>Button</Button>
        </CardBody>
      </Card>
     </Col>
   );

  };
}
 export default Product;
