import React, {Component} from 'react';
import { Card, CardImg, Container, Button, CardTitle, CardText, Row, Col } from 'reactstrap';
import uuid from 'uuid';

import "../App.css"


class ContentProduct extends Component {
    state = {
      products: [
        {id:uuid(), name:'Kacang Thailand', price: '20.000'},
        {id:uuid(), name:'Kacang Atom', price: '20.000'},
        {id:uuid(), name:'Kacang Bawang', price: '20.000'},
        {id:uuid(), name:'Kacang Telur Medan', price: '20.000'},
        {id:uuid(), name:'Kacang Polong', price: '20.000'},
        {id:uuid(), name:'Kacang Polong Manis', price: '20.000'},
      ]
    }

  render () {
    const { products } = this.state
    return (
      <Container className="box">
      <h1>Kacang</h1>
        <Row >
        {products.map(({ id, name, price}) => (
          <Col  xs={12} sm={12} md={12} lg={4}>
           <Card key={id} body className="borderproduct">
            <CardImg top width="100%" src={require(`../assets/kacang thailand.png`)} alt="Card image cap" />
            <br/>
            <CardTitle>{name}</CardTitle>
            <CardText>Rp {price}</CardText>
            <Button outline color="info"> Detail Produk </Button>
            <br/>
           <Button href="https://wa.me/6281294086512?text=Hello...saya ingin mendapatkan informasi produk snack adirasa..%F0%9F%98%8A%F0%9F%98%8A" color="success"> <img src={require(`../assets/whatsapp.png`)} width="10%" /> <br/> Pesan Sekarang </Button>
           </Card>
          </Col>
        ))}
        </Row>
      </Container>
    );
  }
}

export default ContentProduct;
