import React, {Component} from 'react';
import {
   Card,
   CardImg,
   CardTitle,
   Container,
   Button,
   Row,
   Col,
   Media } from 'reactstrap';
import uuid from 'uuid';

import "../App.css"

class KacangProduct extends Component {
    state = {
      products: [
        {id:uuid(), name:'Kacang Atom', label: "Satelite Palapa", price: "160.000", weight: 4, taste:"Gurih"},
        {id:uuid(), name:'Kacang Bawang',label: "Satelite Palapa", price: "160.000", weight: 4, taste:"Gurih"},
        {id:uuid(), name:'Kacang Kedelai',label: "Satelite Palapa", price: "160.000", weight: 4, taste:"Gurih"},
        {id:uuid(), name:'Kacang Koro Kulit', label: "Jaipong", price: "160.000", weight: 4, taste:"Gurih"},
        {id:uuid(), name:'Kacang Kulit', label: "Jaipong", price: "160.000", weight: 4, taste:"Gurih"},
        {id:uuid(), name:'Kacang Mix', label: "Satelite Palapa", price: "160.000", weight: 4, taste:"Gurih" },
        {id:uuid(), name:'Kacang Polong', label: "Jaipong", price: "160.000", weight: 4, taste:"Gurih"},
        {id:uuid(), name:'Kacang Polong Manis', label: "Jaipong", price: "160.000", weight: 4, taste:"Gurih"},
        {id:uuid(), name:'Kacang Telur Medan', label: "Satelite Palapa", price: "160.000", weight: 4, taste:"Gurih" },
        {id:uuid(), name:'Kacang Thailand', label: "Satelite Palapa", price: "160.000", weight: 4, taste:"Pedas"},
      ]
    }

  render () {
    const { products } = this.state
    return (
      <Container className="box">
      <h1>Kacang</h1>
      <br/>
        <Row >
        {products.map(({ id, name, label, price, rasa, weight, taste }) => (
          <Col  key={id} xs={12} sm={12} md={12} lg={6} >
          <br/>
          <Media>
            <Media left href="#">
            <Media top width="160px" src={require(`../assets/kacang thailand.png`)} alt="Card image cap" />
         </Media>
         <br/>
         <Media body>
            <Media heading>{name}</Media>
             <p>Berat Bersih : {weight} gram</p>
             <p>Rp {price} ,-</p>
             <Button outline color="warning">warning</Button>{' '}
             <Button href="/cart" outline color="danger" >Beli Sekarang</Button>
         </Media>
         </Media>
          </Col>
        ))}
        </Row>
      </Container>
    );
  }
}

export default KacangProduct;
