import React, {Component} from 'react';
import {
   Card,
   CardImg,
   Container,
   Button,
   CardTitle,
   CardText,
   Row,
   Col } from 'reactstrap';
import uuid from 'uuid';

import "../App.css"

class KacangProduct extends Component {
    state = {
      products: [
        {id:uuid(), name:'Kacang Thailand', label: "Satelite Palapa", price: "160.000", weight: 4, license: 10928401920, taste:"Pedas" },
        {id:uuid(), name:'Kacang Atom', price: '20.000', weight: 200, license: 10928401920, taste:"Pedas"},
        {id:uuid(), name:'Kacang Bawang', price: '20.000', weight: 200, license: 10928401920, taste:"Pedas"},
        {id:uuid(), name:'Kacang Telur Medan', price: '20.000', weight: 200, license: 10928401920, taste:"Pedas"},
        {id:uuid(), name:'Kacang Polong', price: '20.000', weight: 200, license: 10928401920, taste:"Pedas"},
        {id:uuid(), name:'Kacang Polong Manis', price: '20.000', weight: 200, license: 10928401920, taste:"Pedas"},
      ]
    }

  render () {
    const { products } = this.state
    return (
      <Container className="box">
      <h1>Kacang</h1>
        <Row >
        {products.map(({ id, name, label, price, rasa, weight, taste }) => (
          <Col  xs={12} sm={12} md={12} lg={4}>
           <Card body className="borderproduct">
            <CardImg top width="100%" src={require(`../assets/kacang thailand.png`)} alt="Card image cap" />
            <br/>
            <CardTitle> Nama Product : {name}</CardTitle>
            <CardTitle> Merek : {label} </CardTitle>
            <CardTitle> Rasa : {taste} </CardTitle>
            <CardTitle> Harga : Rp {price} ,- </CardTitle>
            <CardTitle> Berat Bersih : {weight} Kg</CardTitle>
            <br/>
            <Button href="/detailproductkacang" outline color="danger">  Add To Chart </Button>
            <br/>
            <Button href="/" outline color="warning">  Kembali </Button>
           <br/>
           <Button href="https://wa.me/6281294086512?text=Hello...saya ingin mendapatkan informasi produk snack adirasa..%F0%9F%98%8A%F0%9F%98%8A" outline color="success"> <img src={require(`../assets/whatsapp.png`)} width="10%" />  Informasi </Button>
           </Card>
          </Col>
        ))}
        </Row>
      </Container>
    );
  }
}

export default KacangProduct;
