import React, { Component } from 'react';
import {
  Container,
  Button,
  Row,
  Col,
  Card,
  CardImg,
  CardTitle,
  CardSubtitle
   } from 'reactstrap';


import {ProductConsumer} from "../context"

import "../App.css"

class ProductDetail extends Component {
render() {
  return (

      <ProductConsumer>
       {value => {
        const {id, name, img, price, info, weight, taste} = value.detailProducts;
        return(
          <Container>
          <Col  key={id} xs={12} sm={12} md={12} lg={4}>
          <Card outline color="info" className="box">

            <CardImg top width="5%" src={img} alt="Card image cap" />

            <CardBody>
              <CardTitle>{name}</CardTitle>
              <br/>
              <CardSubtitle>Harga : Rp {price} ;-</CardSubtitle>
              <br/>
              <CardSubtitle>Berat Bersih {weight} Kg</CardSubtitle>
            </CardBody>
          </Card>
         </Col>

          </Container>

        )
       }}
      </ProductConsumer>

   );
 };
}

export default ProductDetail;
