import React, {Component} from 'react';
import {
   Card,
   CardImg,
   Container,
   Button,
   CardTitle,
   CardText,
   Row,
   Col } from 'reactstrap';
import uuid from 'uuid';

import "../App.css"

const StyleButton = {
  marginLeft: "15px",
  textAlign: "center"
};

class KacangProduct extends Component {
  constructor(props) {
  super(props);
  state = {
    products: [
      {id:uuid(), name:'Kacang Thailand', price: "20.000", weight: 200 },
      {id:uuid(), name:'Kacang Atom', price: '20.000', weight: 200},
      {id:uuid(), name:'Kacang Bawang', price: '20.000', weight: 200},
      {id:uuid(), name:'Kacang Telur Medan', price: '20.000', weight: 200},
      {id:uuid(), name:'Kacang Polong', price: '20.000', weight: 200},
      {id:uuid(), name:'Kacang Polong Manis', price: '20.000', weight: 200},
    ]
  }
  this.id = this.props.match.params.id;
}


  render () {
    const { products } = this.state
    return (
      <Container className="box">
      <h1>Kacang</h1>
        <Row >
        {products.map(({ id, name, price, rasa, weight}) => (
          <Col  xs={12} sm={12} md={12} lg={4}>
           <Card body className="borderproduct">
            <CardImg top width="100%" src={require(`../assets/kacang thailand.png`)} alt="Card image cap" />
            <br/>
            <CardTitle> Nama Product : {name}</CardTitle>
            <CardTitle> Harga : Rp {price} ,- </CardTitle>
            <CardTitle> Berat Bersih : {weight} gram</CardTitle>
            <br/>
            <Row >
            <Button style={StyleButton} href="/detailproductkacang" outline color="info" food_id={this.id} match={this.props.match.url}>  Detail Product </Button>
            <br/>
            <Button  style={StyleButton} href="/detailproductkacang" outline color="danger">  Add To Chart </Button>
            <br/>
            <Button style={StyleButton} href="/" outline color="warning">  Kembali </Button>
           </Row>
           <br/>
           <Button href="https://wa.me/6281294086512?text=Hello...saya ingin mendapatkan informasi produk snack adirasa..%F0%9F%98%8A%F0%9F%98%8A" outline color="success"> <img src={require(`../assets/whatsapp.png`)} width="10%" />  Informasi </Button>
           </Card>
          </Col>
        ))}
        </Row>
      </Container>
    );
  }
}

export default KacangProduct;
