import React, {Component} from 'react';
import {
   Card,
   CardImg,
   Container,
   Button,
   CardTitle,
   Row,
   Col } from 'reactstrap';
import uuid from 'uuid';

import "../App.css"

class PermenProduct extends Component {
    state = {
      products: [
        {id:uuid(), name:'Kacang Thailand', price: '20.000'},
        {id:uuid(), name:'Kacang Atom', price: '20.000'},
        {id:uuid(), name:'Kacang Bawang', price: '20.000'},
        {id:uuid(), name:'Kacang Telur Medan', price: '20.000'},
        {id:uuid(), name:'Kacang Polong', price: '20.000'},
        {id:uuid(), name:'Kacang Polong Manis', price: '20.000'},
      ]
    }

  render () {
    const { products } = this.state
    return (
      <Container className="box">
      <h1> PERMEN </h1>
      <Row >
      {products.map(({ id, name, label, price, rasa, weight, taste }) => (
        <Col  key={id} xs={12} sm={12} md={12} lg={4}>
         <Card body className="borderproduct">
          <CardImg top width="100%" src={require(`../assets/kacang thailand.png`)} alt="Card image cap" />
          <br/>
          <h2> {name} </h2>
          <br/>
          <CardTitle> Berat Bersih : {weight} gram</CardTitle>
          <CardTitle> Rp {price} ,-  </CardTitle>
          <br/>
          <CardTitle> Order / Informasi Product :</CardTitle>
          <Button href="/cart" outline color="success"> <img src={whatsapp} width="10%" alt="Not Found" />  Beli Sekarang </Button>
          <br/>
          <Button href="/" outline color="warning"> Kembali </Button>
         </Card>
        </Col>
      ))}
      </Row>
      </Container>
    );
  }
}

export default PermenProduct;
