import React, {Component} from 'react';

import { Button,
         Card,
         CardImg,
         CardBody,
         CardTitle,
         CardSubtitle,
         Col,
         Container,
         Navbar,
         NavbarBrand,
         Nav,
         } from 'reactstrap';


import PropTypes from 'prop-types';

import "../App.css";

class Product extends Component {
  render () {
    const {id, name, img, taste, weight } = this.props.product;
    return (
    <Container>
      <Col  key={id} xs={12} sm={12} md={12} lg={4}>
      <Card outline color="info" className="box">
        <CardImg top width="5%" src={img} alt="Card image cap" />
        <CardBody>
          <CardTitle>{name}</CardTitle>
          <CardSubtitle>Rasa : {taste}</CardSubtitle>
          <p> Berat Bersih : {weight} gram</p>
        </CardBody>
      </Card>
     </Col>
     <Navbar color="light fixed-bottom" light expand="md">
         <NavbarBrand  href="/"><img width="5%" src={require(`../assets/left-arrow.png`)}/></NavbarBrand>
         <Button color="warning">warning</Button>{' '}
         <br/>
          <Button color="warning">warning</Button>{' '}
           <Button color="warning">warning</Button>{' '}
       <Nav className="ml-auto" navbar>


       </Nav>

     </Navbar>
    </Container>
   );
  };
}

Product.propTypes = {
  product:PropTypes.shape({
   id:PropTypes.number,
   img:PropTypes.string,
   name:PropTypes.string,
   price:PropTypes.string,
   weight:PropTypes.number
 }).isRequired
}

export default Product;
