import React, {Component} from 'react';

import { Button,
         Card,
         CardImg,
         CardBody,
         CardTitle,
         CardSubtitle,
         Col,
         Container,
         Navbar,
         NavbarBrand,
         Nav,
         Media
         } from 'reactstrap';


import PropTypes from 'prop-types';

import "../App.css";

class Product extends Component {
  render () {
    const {id, name, img, taste, weight } = this.props.product;
    return (
      <Col  key={id} xs={12} sm={12} md={12} lg={4}>
      <Media>
          <Media left href="#">
            <Media width="40%" src={img} alt="Generic placeholder image" />
          </Media>
          <Media body>
            <Media heading>{name}</Media>
            Cras sit amet nibh libero, in gravida nulla. Nulla vel metus scelerisque ante sollicitudin commodo. Cras purus odio, vestibulum in vulputate at, tempus viverra turpis. Fusce condimentum nunc ac nisi vulputate fringilla. Donec lacinia congue felis in faucibus.
          </Media>
      </Media>
      <Card outline color="info" className="box">
        <CardImg left width="5%" src={img} alt="Card image cap" />
        <CardBody>
          <CardTitle>{name}</CardTitle>
          <CardSubtitle>Rasa : {taste}</CardSubtitle>
          <p> Berat Bersih : {weight} gram</p>
        </CardBody>
      </Card>
     </Col>
   );
  };
}

Product.propTypes = {
  product:PropTypes.shape({
   id:PropTypes.number,
   img:PropTypes.string,
   name:PropTypes.string,
   price:PropTypes.string,
   weight:PropTypes.number
 }).isRequired
}

export default Product;
