import React, {Component} from 'react';

import { Card,
         CardImg,
         CardBody,
         CardSubtitle,
         Col,
         } from 'reactstrap';


import PropTypes from 'prop-types';

import "../App.css";

class Product extends Component {
  render () {
    const {id, name, img, taste, price, weight } = this.props.product;
    return (

     <Col key={id} xs={6} sm={12} md={12}lg={4}>
      <Card outline color="info" className="box">
        <CardImg top width="5%" src={img} alt="Card image cap" />
        <CardBody>
          <CardSubtitle>{name}</CardSubtitle>
          <br/>
          <CardSubtitle>Rasa: {taste}</CardSubtitle>
          <CardSubtitle>Berat : {weight} gram</CardSubtitle>
          <p> Rp {price},- </p>



        </CardBody>
      </Card>
     </Col>
   );
  };
}

Product.propTypes = {
  product:PropTypes.shape({
   id:PropTypes.number,
   img:PropTypes.string,
   name:PropTypes.string,
   price:PropTypes.string,
   weight:PropTypes.number
 }).isRequired
}

export default Product;
