import React, { Component } from 'react';
import {
  Container,
  Button,
  Row,
  Col,
  Card,
  CardImg,
  CardTitle,
  CardBody,
  CardSubtitle,
  CardText
   } from 'reactstrap';

import {ProductConsumer} from "../context"

import "../App.css"

class ProductDetail extends Component {
render() {
  return (
      <ProductConsumer>
      {value => {
        const {id, name, img, price, info, weight} =
        value.detailProducts;
        return (
          <Container>
            <Row>
              <Col  key={id} xs={12} sm={12} md={12} lg={4}>
               <h1 className="box">{name}</h1>
              </Col>
            </Row>
          </Container>
        )
      }}
      </ProductConsumer>

   );
 };
}

export default ProductDetail;
