import React, { Component } from 'react';
import {
   Button,
   Container,
   Col,
   Card,
   Form,
   FormGroup,
   Label,
   Modal,
   ModalHeader,
   ModalBody,
   ModalFooter,
   Input} from 'reactstrap';

import axios from "axios";

import "../App.css"

const box = {
  marginTop: "20px",
  marginBottom: "25px",
  position: "relative",
  fontFamily: "Merriweather",
  fontFamily: "serif",
  fontFamily: "Open Sans Condensed",
  fontFamily: "sans-serif",
  textAlign: "center"
}

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000";

class SignUpForm extends Component {
 constructor(props){
   super(props);
    this.state={
      name:"",
      address:"",
      phone: "",
      email: "",
      modal: false
    };
    this.handleChangeName = this.handleChangeName.bind(this);
    this.handleChangeAddress = this.handleChangeAddress.bind(this);
    this.handleChangePhone = this.handleChangePhone.bind(this);
    this.handleChangeEmail = this.handleChangeEmail.bind(this);
    this.submitForm = this.submitForm.bind(this);
    this.toggle = this.toggle.bind(this);
 }

 toggle() {
    this.setState(prevState => ({
      modal: !prevState.modal
    }));
  }

 handleChangeName(event){
   let value = event.target.value;
  this.setState(() => {
    return { name : value };
   });
  }

 handleChangeAddress(event){
   let value = event.target.value;
   this.setState(() => {
     return { address : value};
   });
 }

 handleChangePhone(event){
   let value = event.target.value;
   this.setState(() => {
     return { phone : value};
   });
 }

 handleChangeEmail(event){
   let value = event.target.value;
   this.setState(() => {
     return { email : value};
   })
 }

 // submitForm(event) {
 // event.preventdefault();
//}
submitForm(event) {
  event.preventDefault();
  axios
  .post(`${API_URL}/api/users`, {
    name: this.state.name,
    address: this.state.address,
    phone: this.state.phone,
    email: this.state.email
  })
  .then(res => {
    const id = res.data.data.id;
      this.props.history.push(`/api/users/${id}`);
  })
  .catch(error => {
      alert("Terima Kasih Sudah Mendaftar Sebagai Mitra Kami, Kami akan Menghubungi Kamu Melalui whatsapp");
      console.log(error.res);
    });
  this.setState({ text: "" });
}


render() {

  return (
    <Container>
      <Form
       className="loginForm text-info"
       onSubmit={this.submitForm}>
      <h4>Form Pendaftaran</h4>
         <FormGroup>
         <Label for="exampleName">Nama Lengkap</Label>
          <Input
            type="text"
            name="Name"
            id="exampleFirstName"
            placeholder="Nama Lengkap "
            value={this.state.name}
            onChange={this.handleChangeName}
            />
           </FormGroup>
         <FormGroup>
         <Label for="exampleAddress">Alamat</Label>
          <Input
            type="text"
            name="address"
            id="exampleAddress"
            placeholder="Alamat Pengiriman"
            value={this.state.address}
            onChange={this.handleChangeAddress}/>
         </FormGroup>

         <FormGroup>
            <Label for="exampleEmail">Email</Label>
            <Input
              type="text"
              name="email"
              id="exampleEmail"
              placeholder="Alamat Email"
              value={this.state.email}
              onChange={this.handleChangeEmail}/>
         </FormGroup>

        <FormGroup>
           <Label for="examplePhone">Nomor Telepon WhatsApp</Label>
           <Input
              type="text"
              name="phone"
              id="examplePhone"
              placeholder="Nomor WhatsApp"
              value={this.state.phone}
              onChange={this.handleChangePhone} />
        </FormGroup>

          <Button
             outline color="danger"
             size="lg"
             block
             onClick={this.submitForm}> Daftar Sekarang
          </Button>
          <Button color="danger" onClick={this.toggle}>Daftar Sekarang</Button>
            <Modal isOpen={this.state.modal} modalTransition={{ timeout: 700 }} backdropTransition={{ timeout: 1300 }}
            toggle={this.toggle} className={this.props.className}>
          <ModalHeader toggle={this.toggle}>Modal title</ModalHeader>
          <ModalBody>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
          </ModalBody>
          <ModalFooter>
            <Button color="primary" onClick={this.toggle}>Do Something</Button>{' '}
            <Button color="secondary" onClick={this.toggle}>Cancel</Button>
          </ModalFooter>
        </Modal>

      </Form>
      <Col xs={12} sm={12} md={12} lg={12}>
         <Card outline color="info" body style={box}>
         <p>Setelah Melakukan Pendaftaran, Kami Akan Segera Menghubungi Kamu Melalui WhatsApp</p>
         <h4> <img width="8%" src={require(`../assets/whatsapp.png`)} alt="Card"/> 081294086512</h4>
         </Card>
      </Col>

    </Container>
   );
 };
}

export default SignUpForm;
