import React, { Component } from "react";
import { Card, CardImg, Container, Button, CardTitle, CardText, Row, Col } from 'reactstrap';
import axios from 'axios';

import "../App.css"

const API_URL = process.env.API_URL || "http://localhost:5000";

class ItemProduct extends Component {
  constructor() {
    super();
  state = {
    product: [],
  };
  this.getIndex = this.getIndex.bind(this);
}

  getIndex(index) {
    // console.log(index);
    this.setState(() => {
      return {
        index: index
      };
    });
  }

  async getData() {
    await axios
      .get(`${API_URL}/api/products`)
      .then(res => {
        return res.data;
      })
      .then(res => {
        this.setState({ product: res.data });
        // console.log(this.state.foods);
      });
  }

render() {
  return (
  <Container className="box">
  <h1>Product</h1>
  <Row>
   { this.state.product &&
      this.state.product.map((product, index) => (
        <Col xs={12} sm={12} md={12} lg={4}>
          <Card body className="borderproduct">
          <CardImg top width="100%" src={productImage} alt="Card image cap" />
          <br/>
          <CardTitle> {name} </CardTitle>
          <CardText>Rp {price}</CardText>
          <Button outline color="info"> Detail Produk </Button>{' '}
          <br/>
          <Button outline color="danger"> Beli Sekarang </Button>
          </Card>
        </Col>
    )) }
    </Row>
  </Container>
  );
};
};

export default ItemProduct;
