import React, { Component } from 'react';
import {
   Button,
   Container,
   Col,
   Card,
   Form,
   FormGroup,
   Label,
   Input} from 'reactstrap';

import axios from "axios";

import "../App.css"

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000";

class SignUpForm extends Component {
 constructor(){
   super();
    this.state={
      name:"",
      address:"",
      phone: "",
      email: ""
    };
    this.handleChangeName = this.handleChangeName.bind(this);
    this.handleChangeAddress = this.handleChangeAddress.bind(this);
    this.handleChangePhone = this.handleChangePhone.bind(this);
    this.handleChangeEmail = this.handleChangeEmail.bind(this);
    this.submitForm = this.submitForm.bind(this);
 }

 handleChangeName(event){
   let value = event.target.value;
  this.setState(() => {
    return { name : value };
   });
  }

 handleChangeAddress(event){
   let value = event.target.value;
   this.setState(() => {
     return { address : value};
   });
 }

 handleChangePhone(event){
   let value = event.target.value;
   this.setState(() => {
     return { phone : value};
   });
 }

 handleChangeEmail(event){
   let value = event.target.value;
   this.setState(() => {
     return { email : value};
   })
 }

 // submitForm(event) {
 // event.preventdefault();
//}
submitForm(event) {
  event.preventDefault();
  axios
  .post(`${API_URL}/api/users`, {
    name: this.state.name,
    address: this.state.address,
    phone: this.state.phone,
    email: this.state.email
  })
  .then(res => {
    const id = res.data.data.id;
      this.props.history.push(`/api/users/${id}`);
  })
  .catch(error => {
      alert("Selamat Anda Telah Terdaftar ");
      console.log(error.res);
    });
}


render() {


  return (
    <Container>


      <Form
       className="loginForm text-info"
       onSubmit={this.submitForm}>
      <h1>Form Pendaftaran</h1>
         <FormGroup>
         <Label for="exampleName">Nama Lengkap</Label>
          <Input
            type="text"
            name="Name"
            id="exampleFirstName"
            placeholder="Masukan Nama Lengkap "
            value={this.state.name}
            onChange={this.handleChangeName}
            />
           </FormGroup>

         <FormGroup>
         <Label for="exampleAddress">Alamat</Label>
          <Input
            type="text"
            name="address"
            id="exampleAddress"
            placeholder="Masukan Alamat Pengiriman Barang"
            value={this.state.address}
            onChange={this.handleChangeAddress}/>
         </FormGroup>

         <FormGroup>
            <Label for="exampleEmail">Email</Label>
            <Input
              type="text"
              name="email"
              id="exampleEmail"
              placeholder="Masukan Nomor Telepon Kamu"
              value={this.state.email}
              onChange={this.handleChangeEmail}/>
         </FormGroup>

        <FormGroup>
           <Label for="examplePhone">Nomor Telepon WhatsApp</Label>
           <Input
              type="text"
              name="phone"
              id="examplePhone"
              placeholder="Masukan Nomor Telepon WhatsApp Kamu"
              value={this.state.phone}
              onChange={this.handleChangePhone} />
        </FormGroup>

          <Button
             outline color="danger"
             size="lg"
             block
             onClick={this.submitForm}> Daftar Sekarang
          </Button>
          <Button
             href="/"
             outline color="warning"
             size="lg"
             block> Kembali
          </Button>

      </Form>
      <Col xs={12} sm={12} md={12} lg={12}>
         <Card outline color="info" body className="box">
         <p>Setelah Melakukan Pendaftaran, Kami Akan Segera Menghubungi dan Mengirimkan Katalog Langsung Ke Kamu Melalui WhatsApp</p>
         <h1> : 081294086512</h1>
         </Card>
      </Col>

    </Container>
   );
 };
}

export default SignUpForm;
